    getAccess() {
      this.moduleAccess = this.api.getPageAction();

      if (this.moduleAccess) {
        let addPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'INI') return access.status;
        }).map(function (obj) {
          return obj.status;
        });
        let editPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'EDIT') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        let viewPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'VIW') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        let deletePermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'DEL') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        let recommendPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'REC') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        let approvePermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'APR') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        let printPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'PRI') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        this.permission.add = addPermission.length > 0 ? addPermission[0] : false;
        this.permission.edit = editPermission.length > 0 ? editPermission[0] : false;
        this.permission.view = viewPermission.length > 0 ? viewPermission[0] : false;
        this.permission.delete = deletePermission.length > 0 ? deletePermission[0] : false;
        this.permission.recommend = recommendPermission.length > 0 ? recommendPermission[0] : false;
        this.permission.approve = approvePermission.length > 0 ? approvePermission[0] : false;
        this.permission.print = printPermission.length > 0 ? printPermission[0] : false;
      }

      this.logger.log('this.permission', this.permission);
    }

