    getSubSubSection() {
      if (this.param == undefined) this.param = "";else this.param;
      this.api.getAPI(environment.API_URL + "master/global_sub_sub_section?" + this.param).subscribe(res => {
        this.dataSource = new MatTableDataSource(res.data);
        this.countryList = res.data;
        this.dataSource.paginator = this.pagination;
        this.logger.log('country', this.countryList);
      });
    }

    create() {
      this.crudName = "Add";
      this.isReadonly = false;
      this.editForm.enable();
      let reset = this.formGroupDirective.resetForm();

      if (reset !== null) {
        this.initForm();
      }

      var element = document.getElementById("exampleCheck1");
      element.checked = true;
    }

    editOption(country) {
      this.isReadonly = false;
      this.editForm.enable();
      this.crudName = "Edit";
      this.logger.info(country);
      this.populate(country);
      var element = document.getElementById("exampleCheck1");

      if (this.editForm.value.status == 1) {
        element.checked = true;
      } else {
        element.checked = false;
      }
    }

    onView(country) {
      this.crudName = 'View';
      this.isReadonly = true;
      this.editForm.disable();
      this.populate(country);
      var element = document.getElementById("exampleCheck1");

      if (this.editForm.value.status == 1) {
        element.checked = true;
      } else {
        element.checked = false;
      }
    }

    onDelete(id) {
      let dialogRef = this.dialog.open(ConfirmationDialogComponent, {
        width: '350px',
        data: language[environment.DEFAULT_LANG].confirmMessage
      });
      dialogRef.afterClosed().subscribe(result => {
        if (result) {
          this.api.postAPI(environment.API_URL + "master/global_sub_sub_section/details", {
            id: id,
            status: 3
          }).subscribe(res => {
            this.logger.log('response', res);

            if (res.status == environment.SUCCESS_CODE) {
              this.logger.info('delete');
              this.notification.warn('Sub-Section ' + language[environment.DEFAULT_LANG].deleteMsg);
              this.getSubSubSection();
            } else {
              this.notification.displayMessage(language[environment.DEFAULT_LANG].unableDelete);
            }
          });
        }

        dialogRef = null;
      });
    }

    onSubmit() {
      if (this.editForm.valid) {
        this.editForm.value.created_by = this.api.userid.user_id;
        this.editForm.value.status = this.editForm.value.status == true ? 1 : 2;
        this.api.postAPI(environment.API_URL + "master/global_sub_sub_section/details", this.editForm.value).subscribe(res => {
          this.logger.log('response', res); //this.error= res.status;

          if (res.status == environment.SUCCESS_CODE) {
            // this.logger.log('Formvalue',this.editForm.value);
            this.notification.success(res.message);
            this.getSubSubSection();
            this.closebutton.nativeElement.click();
          } else if (res.status == environment.ERROR_CODE) {
            this.error_msg = true;
            this.ErrorMsg = res.message;
            setTimeout(() => {
              this.error_msg = false;
            }, 2000);
          } else {
            this.notification.displayMessage(language[environment.DEFAULT_LANG].unableSubmit);
          }
        });
      }
    }

    getAccess() {
      this.moduleAccess = this.api.getPageAction();

      if (this.moduleAccess) {
        let addPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'ADD') return access.status;
        }).map(function (obj) {
          return obj.status;
        });
        let editPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'EDIT') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        ;
        let viewPermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'VIW') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        ;
        let deletePermission = this.moduleAccess.filter(function (access) {
          if (access.code == 'DEL') {
            return access.status;
          }
        }).map(function (obj) {
          return obj.status;
        });
        ;
        this.permission.add = addPermission.length > 0 ? addPermission[0] : false;
        this.permission.edit = editPermission.length > 0 ? editPermission[0] : false;
        ;
        this.permission.view = viewPermission.length > 0 ? viewPermission[0] : false;
        ;
        this.permission.delete = deletePermission.length > 0 ? deletePermission[0] : false;
        ;
      }

      this.logger.log('this.permission', this.permission);
    }

    applyFilter(event) {
      this.filterValue = event.target.value;

      if (this.filterValue) {
        this.dataSource.filter = this.filterValue.trim().toLowerCase();
      } else {
        this.getSubSubSection();
      }
    }

    numberOnly(event) {
      var key = event.keyCode;

      if (key > 31 && (key < 65 || key > 90) && (key < 97 || key > 122)) {
        return false;
      }

      return true;
    }

    search() {
      let type = this.searchForm.value.section ? "section_id=" + this.searchForm.value.section : "";
      this.param = type;
      this.getSubSubSection();
    }

    clear() {
      this.searchForm.reset();
      this.param = "";
      this.getSubSubSection();
    }

    chooseCountry(event) {
      this.getSection();
    }

