    onProjectHandler(project_id = '') {
      if (project_id != '') {
        this.currentProject = project_id;
        console.log('this.currentProject', this.currentProject);
        this.api.postAPI(environment.API_URL + "transaction/get_responsibility_section", {
          project_id: project_id
        }).subscribe(res => {
          let sections = res.sections;

          for (let i = 0; i < sections.length; i++) {
            this.addSection(sections[i]);

            for (let j = 0; j < sections[i].units.length; j++) {
              this.addSectionUnit(i, sections[i].units[j].unit);

              for (let x = 0; x < sections[i].units[j].compartments.length; x++) {
                this.addCompartment(i, j, {
                  compartment_id: sections[i].units[j].compartments[x].id,
                  compartment_name: sections[i].units[j].compartments[x].name
                });

                if (sections[i].units[j].compartments[x].compartments.length > 0) {
                  for (let y = 0; y < sections[i].units[j].compartments[x].compartments.length; y++) this.addSpecificCompartments(i, j, x, sections[i].units[j].compartments[x].compartments[y]);
                } else this.addSpecificCompartments(i, j, x, '');
              }

              for (let x = 0; x < sections[i].units[j].equipments.length; x++) {
                this.addEquipment(i, j, {
                  equipment_id: sections[i].units[j].equipments[x].id,
                  equipment_name: sections[i].units[j].equipments[x].name
                });

                if (sections[i].units[j].equipments[x].equipments.length > 0) {
                  for (let y = 0; y < sections[i].units[j].equipments[x].equipments.length; y++) this.addSpecificEquipments(i, j, x, sections[i].units[j].equipments[x].equipments[y]);
                } else this.addSpecificEquipments(i, j, x, '');
              }

              for (let x = 0; x < sections[i].units[j].systems.length; x++) {
                this.addSystem(i, j, {
                  system_id: sections[i].units[j].systems[x].id,
                  system_name: sections[i].units[j].systems[x].name
                });

                if (sections[i].units[j].systems[x].systems.length > 0) {
                  for (let y = 0; y < sections[i].units[j].systems[x].systems.length; y++) this.addSpecificSystems(i, j, x, sections[i].units[j].systems[x].systems[y]);
                } else this.addSpecificSystems(i, j, x, '');
              }
            }
          }

          console.log('crudForm', this.crudForm); // this.sections=res.sections;
        });
      }
    }

